import {
  SET_USER_NAME,
  SET_TOKEN,
} from "./actionTypes";

const initialState = {
  user_name: null,
  token: null

};

export const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER_NAME:
      return {
        ...state,
        user_name: action.payload,
      };
    case SET_TOKEN:
      return {
        ...state,
        token: action.payload,
      };
    default:
      return state;
  }
};
