import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';
import LockIcon from '@material-ui/icons/Lock';
import { Box } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import { Link } from "react-router-dom";
import { fetchPost } from '../utils/fetchApi';
import Cookie from "js-cookie"

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
    width: 350,
    height: 290,
    padding: 20,
    border: '1px solid rgba(255, 255, 255, 0.4)',
    borderRadius: '15px',
    backdropFilter: 'blur(5px)',
    background: 'radial-gradient(at 50% 0%, rgba(0,0,0,0), rgba(255,255,255,0.3))',
    boxShadow: '10px 10px 30px rgba(0,0,0,0.5)'
  },
}));

const CustomForm = ({ buttonText, formSubtitle, formFooterContent, linkText, linkTo }) => {
  const classes = useStyles();


  const onLogIn = async () => {
    const response = await fetchPost('token/', {username: "andrew", password: "bubba1992"})
    const token =  Cookie.get("token") ? Cookie.get("token") : null;
    //to set a cookie
    Cookie.set("token", token);
  }

  return (
    <Box style={{ paddingLeft: 50, paddingTop: 20 }}>
      <Box className={classes.margin}>
        <Grid container spacing={1} alignItems="center" justify="center" >
          <h5 style={{ textAlign: 'center', fontFamily: "monospace", fontSize: '1rem'}}>Silver Bullet<br/>Welcome<br/>{formSubtitle}</h5>
        </Grid>
        <Grid container spacing={1} alignItems="flex-end" justify="center">
          <Grid item>
            <AccountCircle />
          </Grid>
          <Grid item>
            <TextField id="input-with-icon-grid-one" label="Username" />
          </Grid>
        </Grid>
        <Grid container spacing={1} alignItems="flex-end" justify="center">
          <Grid item>
            <LockIcon />
          </Grid>
          <Grid item>
            <TextField id="input-with-icon-grid-two" label="Password" />
          </Grid>
        </Grid>
        <Grid container spacing={1} alignItems="flex-end" justify="center">
          <Box paddingTop={2}>
            <Button onClick={onLogIn} variant="contained" style={{ backdropFilter: 'blur(15px)', background: 'transparent', fontFamily: "monospace" }}>{buttonText}</Button>
          </Box>
        </Grid>
        <Grid container spacing={1} alignItems="center" justify="center" >
          <h5 style={{ textAlign: 'center', fontFamily: "monospace", fontSize: '1rem'}}>{formFooterContent} <Link style={{ textDecoration: 'none' }} to={linkTo}>{linkText}</Link></h5>
        </Grid>
      </Box>
    </Box>
  );
}

export default CustomForm
